template_schema:
{
	  "$schema": "http://json-schema.org/draft-04/schema#", 
	  "required": [
	    "purchase_order_number", 
	    "purchase_order_date", 
	    "pack_number", 
	    "pack_total"
	  ], 
	  "type": "object", 
	  "properties": {
	    "ship_to_address": {
	      "required": [
	        "name", 
	        "address1", 
	        "city", 
	        "state", 
	        "postal_code", 
	        "country"
	      ], 
	      "type": "object", 
	      "description": "Destination address where ordered goods are being shipped to.", 
	      "properties": {
	        "city": {
	          "minLength": 2, 
	          "type": "string", 
	          "description": "Free-form text for city name.", 
	          "maxLength": 30
	        }, 
	        "name": {
	          "minLength": 1, 
	          "type": "string", 
	          "description": "Primary free-form textual description of a location.", 
	          "maxLength": 60
	        }, 
	        "warehouse_number": {
	          "minLength": 2, 
	          "type": "string", 
	          "description": "Unique identifier assigned to a warehouse.", 
	          "maxLength": 80
	        }, 
	        "address1": {
	          "minLength": 1, 
	          "type": "string", 
	          "description": "Address information.", 
	          "maxLength": 55
	        }, 
	        "address2": {
	          "minLength": 1, 
	          "type": "string", 
	          "description": "Address information.", 
	          "maxLength": 55
	        }, 
	        "state": {
	          "minLength": 2, 
	          "type": "string", 
	          "description": "Code[Standard State/Province] as defined by appropriate government agency.", 
	          "maxLength": 2
	        }, 
	        "postal_code": {
	          "minLength": 3, 
	          "type": "string", 
	          "description": "International postal zone excluding punctuation and blanks[Zip Code for United States].", 
	          "maxLength": 15
	        }, 
	        "country": {
	          "minLength": 2, 
	          "type": "string", 
	          "description": "Human readable description identifying the country.", 
	          "maxLength": 3
	        }
	      }
	    }, 
	    "purchase_order_number": {
	      "minLength": 1, 
	      "type": "string", 
	      "description": "Identifying number for the purchase order assigned by the buying organization.", 
	      "maxLength": 22
	    }, 
	    "pack_number": {
	      "type": "number", 
	      "description": "Used to identify the incremental number of a pack within a total."
	    }, 
	    "pack_total": {
	      "type": "number", 
	      "description": "Used to specify the total number of packs in the shipment."
	    }, 
	    "items": {
	      "items": {
	        "required": [
	          "vendor_part_number", 
	          "ship_quantity", 
	          "product_description"
	        ], 
	        "type": "object", 
	        "description": "Encloses all item level elements", 
	        "properties": {
	          "vendor_part_number": {
	            "minLength": 1, 
	            "type": "string", 
	            "description": "Vendor's primary product identifier.", 
	            "maxLength": 48
	          }, 
	          "product_description": {
	            "minLength": 1, 
	            "type": "string", 
	            "description": "Free-form textual description of the product.", 
	            "maxLength": 80
	          }, 
	          "ship_quantity": {
	            "type": "number", 
	            "description": "Quantity that has already or is scheduled to be shipped/delivered."
	          }
	        }
	      }, 
	      "required": [
	        "vendor_part_number", 
	        "ship_quantity", 
	        "product_description"
	      ], 
	      "type": "array"
	    }, 
	    "bill_to_address": {
	      "required": [
	        "name", 
	        "address1", 
	        "city", 
	        "state", 
	        "postal_code", 
	        "country"
	      ], 
	      "type": "object", 
	      "description": "Address for the party being billed or invoiced.", 
	      "properties": {
	        "city": {
	          "minLength": 2, 
	          "type": "string", 
	          "description": "Free-form text for city name.", 
	          "maxLength": 30
	        }, 
	        "name": {
	          "minLength": 1, 
	          "type": "string", 
	          "description": "Primary free-form textual description of a location.", 
	          "maxLength": 60
	        }, 
	        "address1": {
	          "minLength": 1, 
	          "type": "string", 
	          "description": "Address information.", 
	          "maxLength": 55
	        }, 
	        "address2": {
	          "minLength": 1, 
	          "type": "string", 
	          "description": "Address information.", 
	          "maxLength": 55
	        }, 
	        "state": {
	          "minLength": 2, 
	          "type": "string", 
	          "description": "Code[Standard State/Province] as defined by appropriate government agency.", 
	          "maxLength": 2
	        }, 
	        "postal_code": {
	          "minLength": 3, 
	          "type": "string", 
	          "description": "International postal zone excluding punctuation and blanks[Zip Code for United States].", 
	          "maxLength": 15
	        }, 
	        "country": {
	          "minLength": 2, 
	          "type": "string", 
	          "description": "Human readable description identifying the country.", 
	          "maxLength": 3
	        }
	      }
	    }, 
	    "purchase_order_date": {
	      "minLength": 10, 
	      "type": "string", 
	      "description": "Date the purchase order was created.", 
	      "maxLength": 10
	    }, 
	    "carrier": {
	      "items": {
	        "required": [
	          "description"
	        ], 
	        "type": "object", 
	        "description": "Specifies carrier identity, transportation information, and equipment details.", 
	        "properties": {
	          "description": {
	            "minLength": 1, 
	            "type": "string", 
	            "description": "Free-form description of the routing/requested routing for shipment or the originating carrier's identity.", 
	            "maxLength": 35
	          }
	        }
	      }, 
	      "required": [
	        "description"
	      ], 
	      "type": "array"
	    }
	  }, 
	  "title": "packing_slip"
	}
